insert into SUBJECT(subjectId, subjectName, majorId) values ('6', 'Sinh học', '25');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Trong quá trình nhân đôi ADN, trên một mạch khuôn, mạch ADN mới được tổng hợp liên tục, còn trên mạch khuôn còn lại, mạch mới được tổng hợp ngắt quãng theo từng đoạn. Hiện tượng này xảy ra do', 'mạch mới luôn luôn được tổng hợp theo chiều tháo xoắn của ADN', 'mạch mới luôn luôn được tổng hợp theo chiều 3’ 	→ 5’', 'mạch mới luôn luôn được tổng hợp theo chiều 5’ 	→ 3’', 'mạch mới luôn luôn được tổng hợp theo hướng ngược chiều tháo xoắn của ADN', '3');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Tính thoái hóa của mã di truyền là hiện tượng nhiều bộ ba khác nhau cùng mã hóa cho một loại axit amin. Những mã di truyền nào sau đây không có tính thoái hóa?', '5’AUG3’, 5’UGG3’', '5’XAG3’, 5’AUG3’', '5’UUU3’, 5’AUG3’', '5’UXG3’. 5’AGX3’', '1');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Vùng nhân của vi khuẩn Helicobacter pylori có một phân tử ADN và ADN chỉ chứa N14. Đưa một vi khuẩn Helicobacter pylori vào trong môi trường dinh dưỡng chỉ chứa N15 phóng xạ và vi khuẩn sinh sản theo hình thức phân đôi tạo ra 16 vi khuẩn con. Khẳng định nào sau đây đúng?', 'Trong 16 phân tử ADN con được tạo ra ở vùng nhân, có 15 mạch được tổng hợp liên tục, 15 mạch được tổng hợp gián đoạn.', 'Trong 16 phân tử ADN con được tạo ra ở vùng nhân, có 16 mạch được tổng hợp liên tục, 16 mạch được tổng hợp gián đoạn.', 'Trong quá trình nhận đôi ADN, enzim nối ligaza chỉ tác động lên một trong hai mạch đơn mới được tổng hợp từ một phân tử mẹ ADN.', 'Tất cả phân tử ADN ở vùng nhân của các vi khuẩn con đều có những đơn phân chứa N15.', '4');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Tính đặc hiệu của mã di truyền được thể hiện như thế nào?', 'Mọi loài sinh vật đều có chung một bộ mã di truyền', 'Mỗi axit amin thường được mã hóa bởi nhiều bộ ba.', 'Mỗi bộ ba chỉ mã hóa cho một loại axit amin', 'Mã di truyền được dọc theo cụm nối tiếp, không gối nhau.', '3');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Khi nói về quá trình nhân đôi ADN, phát biểu nào sau đây sai?', 'Enzim ADN polimeraza tổng hợp và kéo dài mạch mới theo chiều 3’ 	→ 5’', 'Enzim ligaza (enzim nối) nối các đoạn Okazaki thành mạch đơn hoàn chỉnh', 'Quá trình nhân đôi ADN diễn ra theo nguyên tắc bổ sung và nguyên tắc bán bảo tồn', 'Nhờ các enzim tháo xoắn, hai mạch đơn của ADN tách nhau dần tạo nên chạc chữ Y', '1');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Ở cấp độ phân tử, thông tin di truyền được truyền từ tế bào mẹ sang tế bào con nhờ cơ chế', 'giảm phân và thụ tinh', 'nhân đôi ADN', 'phiên mã', 'dịch mã', '2');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Từ 3 loại nicleotit khác nhau sẽ tạo được nhiều nhất bao nhiêu loại bộ mã khác nhau?', '27', '48', '16', '9', '1');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Enzim ADN polimeraza có vai trò gì trong quá trình tái bản ADN?', 'Sử dụng đồng thười cả 2 mạch khuôn để tổng hợp ADN mới.', 'Lắp ráp nucleotit tự do theo nguyên tắc bổ sung với mỗi mạch khuôn của ADN theo chiều 5’ 	→ 3’', 'Sử dụng một mạch khuôn để tổng hợp mạch mới theo chiều 3’ 	→ 5’', 'Chỉ xúc tác tháo xoắn ADN mà không tổng hợp mạch mới', '2');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Một gen dài 5100Ao, số nucleotit loại A của gen bằng 2/3 số lượng một loại nucleotit khác. Gen này thực hiện tái bản liên tiếp 4 lần. Số nucleotit mỗi loại mà môi trường nội bào cung cấp cho quá trình tái bản trên là:', 'A=T= 9000; G=X=13500', 'A=T= 2400; G=X=3600', 'A=T=9600; G=X=14400', 'A=T=18000; G=X=27000', '1');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Dựa vào đâu để phân loại gen cấu trúc và gen điều hòa?', 'Dựa vào cấu trúc của gen', 'Dựa vào sự biểu hiện kiểu hình của gen', 'Dựa vào kiểu tác động của gen', 'Dựa vào chức năng sản phẩm của gen', '4');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Nguyên tắc bán bảo tồn được thể hiện trong cơ chế nhân đôi ADN có nghĩa là:', 'trong 2 phân tử ADN mới được hình thành, mỗi phân tử gồm có 1 mạch là của ADN ban đầu và 1 mạch mới tổng hợp.', 'sự nhân đôi xảy ra trên 2 mạch của phân tử ADN theo 2 hướng và ngược chiều nhau.', 'trong 2 phân tử ADN mới được hình thành, 1 phân tử giống với phân tử ADN mẹ còn phân tử kia có cấu trúc thay đổi.', '2 phân tử ADN mới được hình thành hoàn toàn giống nhau và giống với ADN mẹ ban đầu.', '1');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Điều nào dưới đây là đúng để giải thích vì sao trên mỗi chạc chữ Y chỉ có một mạch của phân tử ADN được tổng hợp liên tục còn một mạch được tổng hợp gián đoạn?', 'Do 2 mạch khuôn có cấu trúc ngược chiều nhưng enzim ADN polimeraza chỉ xúc tác tổng hợp mạch mới theo chiều 5’ 	→ 3’.', 'Sự liên kết các nucleotit trên 2 mạch diễn ra không đồng thời.', 'Do giữa 2 mạch có nhiều liên kết bổ sung khác nhau.', 'Do 2 mạch khuôn có cấu trúc ngược chiều nhưng enzim ADN polimeraza chỉ xúc tác tổng hợp mạch mới theo chiều 3’ 	→ 5’.', '1');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Người ta sử dụng một chuỗi polinucleotit có  làm khuôn để tổng hợp nhân tạo một chuỗi polinucleotit bổ sung có chiều dài bằng chiều dài của chuỗi khuôn đó. Tính theo lí thuyết, tỉ lệ các loại nucleotit tự do cần cung cấp cho quá trình tổng hợp này là:', 'A+G=80%; T+X=20%', 'A+G=20%; T+X=80%', 'A+G=25%; T+X=75%', 'A+G= 75%; T+X=25%', '2');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Một gen gồm 150 vòng xoắn và có 3900 liên kết hidro, nhân đôi liên tiếp 3 lần. Số nucleotit tự do mỗi loại mà môi trường nội bào cần cung cấp cho quá trình này là:', 'A = T = 4200; G = X = 6300', 'A = T = 5600; G = X = 1600', 'A = T = 2100; G = X = 600', 'A = T = 4200; G = X = 1200', '1');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Một gen ở sinh vật nhân thực có 3900 liên kết hidro và có 900 nucleotit loại guanine. Mạch 1 của gen có số nucleotit loại adenine chiếm 30% và số nucleotit loại guanine chiếm 10% tổng số nucleotit của mạch. Số nucleotit mỗi loại ở mạch 1 của gen này là:', 'A=450; T=150; G=150; X=750', 'A=750; T=150; G=150; X=150', 'A=450; T=150; G=750; X=150', 'A=150; T=450; G=750; X=150', '1');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Loại enzim nào sau đây trực tiếp tham gia vào quá trình phiên mã các gen cấu trúc ở sinh vật nhân sơ?', 'ADN polimeraza', 'Ligaza', 'Restrictaza', 'ARN polimeraza', '4');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Sự hoạt động đồng thời của nhiều riboxom trên cùng một phân tử mARN có vai trò', 'làm tăng năng suất tổng hợp protein cùng loại', 'đảm bảo cho quá trình dịch mã diễn ra chính xác', 'đảm bảo cho quá trình dịch mã diễn ra liên tục', 'làm tăng năng suất tổng hợp protein khác loại', '1');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Mạch khuôn của gen có đoạn 3’ TATGGGXATGTA 5’ thì mARN được phiên mã từ mạch khuôn này có trình tự nucleotit là', '3’AUAXXXGUAXAU5’', '5’AUAXXXGUAXAU3’', '3’ATAXXXGTAXAT5’', '5’ATAXXXGTAXAT3’', '2');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Một trong những điểm giống nhau giữa quá trình nhân đôi ADN và quá trình phiên mã ở sinh vật nhân thực là:', 'đều có sự xúc tác của enzim ADN polimeraza để lắp ráp với các nucleotit trên mạch khuôn theo nguyên tắc bổ sung.', 'các quá trình thường thực hiện một lần trong một tế bào.', 'diễn ra trên toàn bộ phân tử ADN.', 'việc lắp ghép các đơn phân được thực hiện trên cơ sở nguyên tắc bổ sung.', '4');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Chiều của mạch khuôn trên ADN được dùng để tổng hợp mARN và chiều tổng hợp mARN lần lượt là:', '5’ 	→ 3’ và 5’ 	→ 3’', '3’ 	→ 5’ và 3’ 	→ 5’', '5’ 	→ 3’ và 3’ 	→ 5’', '3’ 	→ 5’ và 5’ 	→ 3’', '4');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Ở sinh vật nhân sơ và sinh vật nhân thực có sự khác nhau về axit amin mở đầu cho việc tổng hợp chuỗi polipeptit. Sự sai khác đó là:', 'ở sinh vật nhân sơ, axit amin mở đầu là foocmin metonin còn ở sinh vật nhân thực là metionin.', 'ở sinh vật nhân thực là axit amin foocmin metionin còn ở sinh vật nhân sơ là metionin.', 'ở sinh vật nhân sơ là axit amin foocmin metionin còn ở sinh vật nhân thực là valin.', 'ở sinh vật nhân sơ là axit amin foocmin metionin còn ở sinh vật nhân thực là glutamic.', '1');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Cho đoạn mạch gốc của phân tử ADN có trình tự nucleotit như sau:', '7', '5', '3', '1', '1');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Trên tARN thì bộ ba đối mã (anticodon) có nhiệm vụ', 'xúc tác hình thành liên kết giữa axit amin với tARN', 'xúc tác vận chuyển axit amin đến nơi tổng hợp protein', 'xúc tác hình thành liên kết peptit', 'nhận biết codon đặc hiệu trên mARN trong quá trình tổng hợp protein.', '4');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Chiều dài của gen D ở sinh vật nhân sơ là 510 nm. Mạch 1 của nó có 400 nucleotit loại A, 500 nucleotit loại T và 400 nucleotit loại G. Phân tử mARN có chiều dài tương ứng vừa được tổng hợp trên mạch 2 của gen D có số nucleotit từng loại là bao nhiêu?', 'U=300; G=400; X=200; A=600', 'U=200; G=400; X=200; A=700', 'U=400; G=200; X=400; A=500', 'U=500; G=400; X=200; A=400', '4');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Một gen ở sinh vậy nhân sơ có 2025 liên kết hidro, mARN do gen đó tổng hợp có G – A = 125 nucleotit; X – U = 175 nucleotit. Được biết tất cả số nucleotit loại T của gen đều tập trung trên mạch mã gốc. Số nucleotit mỗi loại trên mARN là', 'A=225; G=350; X=175; U=0', 'A=350; G=225; X=175; U=0', 'A=175; G=225; X=350; U=0', 'U=225; G=350; X=175; A=0', '1');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Cho biết các codon mã hóa các axit amin tương ứng như sau: GGG – Gly; XXX – Pro; GXU – Ala; XGA- Arg; UXG – Ser; AGX – Ser. Một đoạn mạch gốc của một gen ở vi khuẩn có trình tự các nucleotit là 5’ AGXXGAXXXGGG 3’. Nếu đoạn mạch gốc này mang thông tin mã hóa cho đoạn polipeptit có 4 axit amin thì trình tự của 4 axit amin đó là:', 'Pro – Gly – Ser – Ala.', 'Ser – Ala – Gly – Pro.', 'Gly – Pro – Ser – Arg.', 'Ser – Arg – Pro – Gly.', '1');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Khi nói về số lần nhân đôi và số lần phiên mã của các gen ở một tế bào nhân thực, trong trường hợp không có đột biến, phát biểu nào sau đây là đúng?', 'Các gen trên các NST khác nhau có số lần nhân đôi bằng nhau và số lần phiên mã thường khác nhau.', 'Các gen nằm trên cùng một NST có số lần nhân đôi khác nhau và số lần phiên mã thường khác nhau.', 'Các gen trên các NST khác nhau có số lần nhân đôi bằng nhau và số lần phiên mã thường bằng nhau.', 'Các gen nằm trong một tế bào có số lần nhân đôi bằng nhau và số lần phiên mã bằng nhau.', '1');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Sự điều hòa hoạt động của gen tổng hợp enzim phân giải lactozo của vi khuẩn E. coli diễn ra ở cấp độ nào?', 'Diễn ra hoàn toàn ở cấp độ sau dịch mã.', 'Diễn ra hoàn toàn ở cấp độ dịch mã.', 'Diễn ra chủ yếu ở cấp độ phiên mã.', 'Diễn ra hoàn toàn ở cấp độ sau phiên mã.', '3');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Mối tương quan giữa protein ức chế với vùng vận hành O được thể hiện như thế nào?', 'Khi môi trường không có lactozo, protein ức chế gắn vào O, ngăn cản sự phiên mã của nhóm gen cấu trúc.', 'Khi môi trường không có lactozo, protein ức chế không gắn được vào O, không diễn ra sự phiên mã của nhóm gen cấu trúc.', 'Khi môi trường không có lactozo, protein ức chế không gắn được vào O, enzim phiên mã có thể liên kết được với vùng khởi động để tiến hành phiên mã nhóm gen cấu trúc.', 'Khi môi trường có lactozo, protein ức chế gắn vào O, ngăn cản sự phiên mã của nhóm gen cấu trúc.', '1');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Theo giai đoạn phát triển của cá thể và theo nhu cầu hoạt động sống của tế bào thì', 'tất cả các gen trong tế bào đều hoạt động', 'tất cả các gen trong tế bào có lúc đồng loạy hoạt động, có khi đồng loạt dừng', 'chỉ có một số gen trong tế bào hoạt động', 'phần lớn các gen trong tế bào hoạt động', '3');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Trình tự các thành phần cấu tạo của một operon là', 'gen điều hòa – vùng vận hành – vùng khởi động – nhóm gen cấu trúc.', 'vùng khởi động – vùng vận hành – nhóm gen cấu trúc.', 'nhóm gen cấu trúc – vùng vận hành – vùng khởi động.', 'nhóm gen cấu trúc – vùng khởi động – vùng vận hành.', '2');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Phát biểu nào dưới đây không đúng khi nói về đặc điểm của điều hòa hoạt động gen ở tế bào nhân thực?', 'Cơ chế điều hòa hoạt động gen ở sinh vật nhân thực phức tạp hơn ở sinh vật nhân sơ.', 'Phần lớn các trình tự nucleotit trên ADN được mã hóa thành các thông tin di truyền.', 'Phần ADN không mã hóa thì đóng vai trò điều hòa hoặc không hoạt động.', 'Có nhiều mức điều hòa, qua nhiều giai đoạn: từ nhiễm sắc thể (NST) tháo xoắn đến biến đổi sau dịch mã.', '2');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Trong cơ chế điều hòa hoạt động của operon Lac, sự kiện nào sau đây diễn ra cả khi môi trường có lactozo và khi môi trường không có laztozo?', 'Một số phân tử lactozo liên kết với protein ức chế.', 'Gen điều hòa R tổng hợp protein ức chế.', 'Các gen cấu trúc Z, Y, A phiên mã hóa tạo ra các phân tử mARN tương ứng.', 'ARN polimeraza liên kết với vùng khởi động của operon Lac và tiến hành phiên mã.', '2');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Trong cơ chế điều hòa hoạt động gen ở sinh vật nhân sơ, vai trò của gen điều hòa là', 'mang thông tin cho việc tổng hợp một protein ức chế tác động lên các gen cấu trúc.', 'nơi gắn vào của protein ức chế để cản trở hoạt động của enzim phiên mã.', 'mang thông tin cho việc tổng hợp một protein ức chế tác động lên vùng vận hành.', 'mang thông tin cho việc tổng hợp một protein ức chế tác động lên vùng khởi động.', '3');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Trình tự nucleotit đặc biệt của một operon nơi enzim ARN polimeraza bám vào khởi động quá trình phiên mã các gen cấu trúc được gọi là:', 'gen điều hòa.', 'vùng mã hóa.', 'vùng vận hành.', 'vùng khởi động.', '4');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Vai trò của lactozo trong cơ chế điều hòa hoạt động của operon Lac ở E. coli là', 'liên kết đặc hiệu với protein điều hòa, khiến nó mất khả năng bám vào trình tự vận hành, tạo điều kiện cho ARN polimeraza hoạt động.', 'gắn vào trình tự vận hành operator để khởi đầu quá trình phiên mã ở gen điều hòa.', 'hoạt hóa trình tự khởi động promoter để thực hiện quá trình phiên mã ở gen điều hòa.', 'ức chế gen điều hòa và cản trở quá trình phiên mã của gen này để tạo ra protein điều hòa.', '1');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Ở vi khuẩn E. coli, khi nói về hoạt động của các gen cấu trúc trong operon Lac, kết luận nào sau đây đúng?', 'Các gen này có số lần nhân đôi bằng nhau nhưng số lần phiên mã khác nhau.', 'Các gen này có số lần nhân đôi bằng nhau và số lần phiên mã bằng nhau.', 'Các gen này có số lần nhân đôi khác nhau nhưng số lần phiên mã bằng nhau.', 'Các gen này có số lần nhân đôi khác nhau và số lần phiên mã khác nhau.', '2');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Đột biến điểm là những biến đổi trong cấu trúc của gen xảy ra tại', 'một điểm nào đó trên phân tử axit nucleic, liên quan tới một hoặc môt vài cặp nucleotit.', 'một điểm nào đó trên phân tử axit nucleic, liên quan tới một hoặc môt vài nucleotit.', 'nhiều điểm trên phân tử axit nucleic, liên quan tới một số cặp nucleotit.', 'một điểm nào đó trên phân tử ADN, liên quan tới một cặp nucleotit.', '4');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Sự khác nhau giữa đột biến và thể đột biến là:', 'đột biến là những biến đổi trong vật chất di truyền, còn thể đột biến là cơ thể mang đột biến đã biểu hiện ra kiểu hình.', 'đột biến là do biễn đổi trong vật chất di truyền, còn thể đột biến là cơ thể mang đột biến gen lặn tiềm ẩn ở trạng thái dị hợp tử.', 'đột biến luôn xảy ra ở sinh vật, còn thể đột biến chỉ có trong quá trình phân bào tạo ra các giao tử không tham gia thụ tinh.', 'đột biến là do biến đổi trong vật chất di truyền, còn thể đột biến là cơ thể mang đột biến gen ở trạng thái dị hợp.', '1');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Sự biểu hiện kiểu hình của đột biến gen trong đời cá thể xảy ra như thế nào?', 'Đột biến gen trội chỉ biểu hiện khi ở trạng thái đồng hợp tử.', 'Đột biến gen lặn không được biểu hiện.', 'Đột biến gen lặn chỉ biểu hiện khi ở trạng thái dị hợp.', 'Đột biến gen trội biểu hiện cả khi ở trạng thái đồng hợp tử và dị hợp tử.', '4');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Hóa chất gây đột biến 5-BU thường gây đột biến gen dạng', 'thay thế cặp G – X bằng T – A.', 'thay thế cặp G – X bằng cặp X – G.', 'thay thế cặp A – T bằng T – A.', 'thay thế cặp A – T bằng G – X.', '4');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Phát biểu không đúng về đột biến gen là:', 'Đột biến gen làm biến đổi một hoặc một số cặp nucleotit trong cấu trúc của gen.', 'Đột biến gen làm phát sinh các alen mới trong quần thể.', 'Đột biến gen làm biến đổi đột ngột một hoặc một số tính trạng nào đó trên cơ thể sinh vật.', 'Đột biến gen làm thay đổi vị trí của gen trên NST.', '4');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Đột biến gen có thể xảy ra trong quá trình', 'nguyên phân ở tế bào sinh dưỡng và tế bào sinh dục.', 'nguyên phân và giảm phân ở tế bào sinh dưỡng.', 'giảm phân ở tế bào sinh dưỡng và tế bào sinh dục.', 'nguyên phân ở tế bào sinh dưỡng và giảm phân ở tế bào sinh dục.', '4');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Phát biểu nào sau đây là đúng khi nói về đột biến gen?', 'Đột biến xảy ra ở tế bào xoma (đột biến xoma) được nhân lên ở một mô và luôn biểu hiện ở một phần cơ thể.', 'Đột biến gen xảy ra trong những lần nguyên phân đầu tiên của hợp tử (đột biến tiền phôi) thường biểu hiện ra kiểu hình cơ thể', 'Đột biến xảy ra trong giảm phân hình thành giao tử thường không biểu hiện ra kiểu hình ở thế hệ đầu tiên vì ở trạng thái dị hợp.', 'Đột biến xoma chỉ có thể di truyền bằng sinh sản sinh dưỡng và nếu là gen lặn sẽ không biểu hiện ra kiểu hình.', '4');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Đột biến gen thường gây hại cho cơ thể mang đột biến vì', 'làm biến đổi cấu trúc gen dẫn tới cơ thể sinh vật không kiểm soát được quá trình tái bản của gen.', 'làm sai lệch thông tin di truyền dẫn tới làm rối loạn quá trình sinh tổng hợp protein.', 'làm ngừng trệ quá trình phiên mã, không tổng hợp được protein.', 'gen bị biến đổi dẫn tới không truyền đạt được vật chất di truyền qua các thế hệ.', '2');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Một đột biến làm giảm chiều dài của gen đi 10,2A° và mất 8 liên kết hidro. Khi gen ban đầu và gen đột biến đồng thời nhân đôi 3 lần liên tiếp thì số nucleotit mỗi loại môi trường nội bào cung cấp cho gen đột biến giảm đi so với gen ban đầu là:', 'A=T=8; G=X=16.', 'A=T=16; G=X=8', 'A=T=7; G=X=14', 'A=T=14; G=X=7', '3');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Guanin dạng hiếm (G*) kết cặp với timin (T) trong quá trình nhân đôi ADN, tạo nên đột biến điểm dạng', 'thêm một cặp G – X.', 'thay thế cặp A – T bằng cặp G – X.', 'mất một cặp A – T.', 'thay thế cặp G – X bằng cặp A – T.', '4');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Một gen dài 3060A° có tỉ lệ A/G = 4/5. Đột biến xảy ra không làm thay đổi chiều dài của gen nhưng tỉ lệ A/G 	≈ 79,28%. Loại đột biến đó là', 'thay thế 2 cặp A – T bằng 2 cặp G – X.', 'thay thế 1 cặp A – T bằng 1 cặp G – X.', 'thay thế 2 nucleotit loại A bằng 2 nucleotit loại G.', 'đảo vị trí cặp A – T và G – X ở 2 bộ ba khác nhau.', '1');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Một đoạn ADN mạch kép có 4050 liên kết hidro, biết rằng trong đó hàm lượng nucleotit loại T chiếm 15%. Khi gen bị đột biến, tỉ lệ A/G của gen là 43,27%. Nếu chiều dài của gen đột biến không đổi so với gen bình thường thì đột biến gen thuộc dạng', 'thay thế 1 cặp G – X bằng 1 cặp A - T', 'thay thế 2 cặp G – X bằng 2 cặp A - T', 'thay thế 3 cặp G – X bằng 3 cặp A - T', 'thay thế 4 cặp G – X bằng 4 cặp A - T', '3');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Cấu trúc của một nucleoxom gồm', 'một đoạn phân tử ADN quấn 11/4 vòng quanh khối cầu gồm 8 phân tử histon.', 'phân tử ADN quấn 7/4 vòng quanh khối cầu gồm 8 phan tử histon.', 'phân tử histon được quấn quanh bởi 1 đoạn ADN dài 146 nucleotit.', '8 phân tử histon được quấn quanh bởi 7/4 vòng xoắn ADN dài 146 cặp nucleotit', '4');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Quan sát nhiều tế bào sinh dưỡng của một châu chấu có kiểu hình bình thường dưới kính hiển vi vào kì giữa, người ta thấy các tế bào chỉ có 23NST. Kết luận nào sau đây đúng nhất về cá thể châu chấu nói trên?', 'Đó là châu chấu đực do ở châu chấu đực, cặp NST giới tính chỉ có một chiếc.', 'Đó là châu chấu đực do NST giới tính chỉ có một chiếc.', 'Đó là châu chấu đực do bị đột biến làm mấy đi một NST.', 'Có thể là châu chấu đực hoặc cái do đột biến làm mất đi 1 NST.', '1');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Phát biểu nào sau đây không đúng khi nói về NST trong tế bào sinh dưỡng của các loài?', 'Mỗi loài có bộ NST đặc trưng về số lượng, hình thái và cấu trúc.', 'NST thường bao giờ cũng tồn tạo thành từng cặp tương đồng và có số lượng nhiều hơn NST giới tính.', 'NST giới tính chỉ có một cặp có thể tương đồng hoặc không tương đồng, ở một số loài NST giới tính chỉ có một chiếc.', 'Cặp NST giới tính ở giới cái bao giờ cũng gồm 2 chiếc có thể tương đồng hoặc không tương đồng.', '4');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Cơ chế phát sinh đột biến cấu trúc NST là', 'làm đứt gãy NST, tiếp hợp hoặc trao đổi chéo không đều giữa các cromatit.', 'quá trình tổng hợp protein hinfht hành thoi phân bào bị ức chế ở kì đầu của phân bào.', 'rối loạn quá trình nhân đôi của ADN trong quá trình phân bào.', 'làm đứt gãy NST dẫn đến rối loạn sự tiếp hợp trong giảm phân I.', '1');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Tế bào sinh dưỡng của một loài A có bộ NST 2n = 20. Trong tế bào sinh dưỡng ở một cá thể của loài này có tổng số NST là 19 và hàm lượng  ADN không đổi. Tế bào đó đã xảy ra hiện tượng', 'mất NST', 'dung hợp 2 NST với nhau', 'chuyển đoạn NST', 'lặp đoạn NST', '2');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Cho hai NST có cấu trúc và trình tự các gen ABCDE*FGH và MNOPQ*R (dấu * biểu hiện cho tâm động). Đột biến tạo ra NST có cấu trúc MNOCDE*FGH và ABPQ*R thuộc đạng dột biến', 'đảo đoạn ngoài tâm động', 'đảo đoạn có tâm động', 'chuyển đoạn không tương hỗ', 'chuyển đoạn tương hỗ', '4');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Người ta có thể sử dụng dạng đột biến cấu trúc nào sau đây để loại bỏ những gen không mong muốn ra khỏi NST trong chọn giống cây trồng?', 'Đột biến chuyển đoạn NST', 'Đột biến mất đoạn NST', 'Đột biến đảo đoạn NST', 'Đột biến lặp đoạn NST', '2');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Khi nói về đột biến cấu trúc NST, phát biểu nào sau đây đúng?', 'Đột biến chuyển đoạn trên 1 NST có thể làm tăng số lượng gen trên NST.', 'Đột biến lặp đoạn NST có thể làm cho 2 gen alen cùng nằm trên 1 NST.', 'Đột biến đảo đoạn không làm thay đổi hình dạng NST.', 'Đột biến mất đoạn NST thường xảy ra ở động vật mà ít gặp ở thực vật.', '2');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Khi nói về vai trò của đột biến cấu trúc NST đối với tiến hóa và chọn giống, phát biểu nào sau đây không đúng?', 'Có thể dùng đột biến chuyển đoạn tạo các dòng côn trùng giảm khả năng sinh sản.', 'Dùng đột biến mất đoạn nhỏ để loại bỏ những gen không mong muốn ra khỏi cơ thể động vật.', 'Đột biến đảo đoạn góp phần tạo nên các nòi trong loài.', 'Đột biến lặp đoạn tạo điều kiện cho đột biến gen phát sinh alen mới.', '2');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Dạng đột biến số lượng NST gây ra hội chững Đao là', 'thể một ở cặp NST 23, có 45 NST.', 'thể ba ở cặp NST 21, có 47 NST.', 'thể một ở cặp NST 21, có 45 NST.', 'thể ba ở cặp NST 23, có 47 NST.', '2');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Đặc điểm khác nhau cơ bản giữa thể tự đa bội và thể dị đa bội có bộ NST với số lượng bằng nhau trong tế bào là', 'số lượng NST', 'nguồn gốc NST', 'hình dạng NST', 'kích thước NST', '2');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Đặc điểm nào dưới đây không đúng đối với thể đột biến đa bội?', 'Sinh tổng hợp các chất mạnh', 'Cơ quan sinh dưỡng lớn, chống chịu tốt', 'Thường gặp ở thực vật', 'Không có khả năng sinh giao tử bình thường', '4');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Sự không phân li của một cặp NST tương đồng ở một nhóm tế bào sinh dưỡng của một cơ thể khi tiến hành nguyên phân sẽ dẫn đến kết quả', 'tất cả các tế bào của cơ thể đều mang đột biến', 'chỉ có cơ quan sinh dục mang tế bào đột biến', 'tất cả các tế bào sinh dưỡng đều mang đột biến, còn tế bào sinh dục thì không', 'cơ thể sẽ có 2 dòng tế bào: dòng bình thường và dòng mang đột biến', '4');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Bộ NST lưỡng bội của một loài thực vật là 48. Khi quan sát NST trong tế bào sinh dưỡng, người ta thấy có 47 NST. Đột biến trên thuộc dạng', 'đột biến lệch bội', 'đột biến tự đa bội', 'đột biến dị đa bội', 'thể tam nhiễm', '1');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Trường hợp nào dưới đây không thuộc dạng đột biến lệch bội?', 'Tế bào sinh dưỡng có một cặp NST gồm 4 chiếc', 'Trong tế bào sinh dưỡng thì mỗi cặp NST đều chứa 3 chiếc.', 'Tế bào sinh dưỡng thiếu 1 NST trong bộ NST', 'Tế bào sinh dục thừa 1 NST', '2');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Trong trường hợp xảy ra rối loạn phân bào giảm phân I, các loại giao tử được tạo ra từ cơ thể mang kiểu gen XY là', 'XX, XY và O', 'XX, Y và O', 'XY và O', 'X, YY và O', '3');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Ở một loài thực vật, alen A quy định quả đỏ, a quy định quả vàng. Ở thể tam nhiễm, hạt phấn (n+1) không cạnh tranh được với hạt phấn n, còn các loại tế bào noãn đều có khả năng thụ tinh. Phép lai nào dưới đây cho tỉ lệ ở đời con là 2 cây quả đỏ : 1 cây quả vàng?', 'Mẹ Aaa x Bố AA', 'Mẹ Aa x Bố Aaa', 'Mẹ AAa x Bố AA', 'Mẹ Aa x Bố AAA', '2');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Cơ thể tứ bội được tạo thành không phải do', 'sự thụ tinh của 2 giao tử 2n thuộc 2 cá thể khác nhau', 'sự tạo thành giao tử 2n từ thể lưỡng bội và sự thụ tinh của hai giao tử này', 'NST ở hợp tử nhân đôi nhưng không phân li', 'NST ở tế bào sinh dưỡng nhân đôi nhưng không phân li', '4');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Cơ chế phát sinh các giao tử (n-1) và (n+1) là do', 'một cặp NST tương đồng không phân li ở kì sau của giảm phân', 'một cặp NST tương đồng không được nhân đôi', 'thoi phân bào không được hình thành', 'cặp NST tương đồng không xếp song song ở kì giữa I của giảm phân', '1');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Phương pháp nào sau đây có thể tạo ra cây tam bội mang kiểu gen Aaa?', 'Tác động consixin trong quá trình nguyên phân của cây Aa', 'Gây đột biến đa bội trong qúa trình giảm phân của cả bố mẹ trong phép lai Aa x Aa.', 'Gây đột biến đa bội trong qúa trình giảm phân của bố hoặc mẹ trong phép lai Aa x Aa.', 'Gây đột biến đa bội trong qúa trình giảm phân của một bên bố hoặc mẹ trong phép lai Aa x Aa.', '4');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Một hợp tử của một loài nguyên phân bình thường 3 đợt, môi trường đã cung cấp nguyên liệu tương đương với 147 NST đơn. Biết rằng loài nói trên có bộ NST lưỡng bội 2n = 14. Cơ chế đã tạo ra hợp tử nói trên là', 'không hình thành thoi phân bào trong quá trình nguyên phân', 'không hình thành thoi phân bào trong giảm phân ở tế bào sinh giao tử của cả bố và mẹ', 'không hình thành thoi phân bào ở tế bào sinh giao tử của bố hoặc của mẹ khi giảm phân', 'một cặp NST nào đó đã không phân li trong giảm phân', '3');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Giao phấn cây cà chua lưỡng bội thuần chủng có quả đỏ với cây cà chua lưỡng bội quả vàng thu được F1 đều có quả đỏ. Xử lí consixin để tứ bội hóa các cây F1 rồi chọn hai cây F1 để giao phấn với nhau. Ở F2 thu được 253 cây quả đỏ và 23 cây quả vàng. Phát biểu nào sau đây là đúng về hai cây F1 nói trên?', 'Một cây là 4n và cây còn lại là 2n do tứ bội hóa không thành công', 'Cả 2 cây F1 đều là 4n do tứ bội hóa đều thành công', 'Cả 2 cây F2 đều là 2n do tứ bội hóa không thành công', 'Có 1 cây là 4n và 1 cây là 3n', '1');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Ở một loài thực vật, bộ NST lưỡng bội 2n = 16. Quá trình nguyên phân liên tiếp 5 lần từ tế bào sinh dục sơ khai đã cần môi trường cung cấp nguyên liệu tương đương 527 NST. Tế bào sinh dục sơ khai có bộ NST như thế nào?', 'Tế bào có bộ NST là 2n+ 1', 'Tế bào có bộ NST là 2n', 'Tế bào có bộ NST là 2n - 1', 'Tế bào có bộ NST là 2n +2', '1');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Một đoạn polipeptit gồm 4 axit amin có trình tự: Val – Trp – Lys – Pro. Biết rắng các codon mã hóa các axit amin tương ứng như sau: Trp – UGG; Val – GUU; Lys – AAG; Pro – XXA. Đoạn mạch gốc của gen mang thông tin mã hóa cho đoạn polipeptit nói trên có trình tự nucleotit là:', '5’ XAA – AXX – TTX – GGT 3’', '5’ TGG – XTT – XXA – AAX 3’', '5’ GUU – UGG – AAG – XXA 3’', '5’ GTT – TGG – AAG – XXA 3’', '2');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Một gen ở tế bào nhân sơ có 2400 nucleotit. Trên mạch 1 của gen, hiệu số tỉ lệ % giữa A với T bằng 20% số nucleotit của mạch. Trên mạch 2 có số nucleotit loại A chiếm 15% số nucleotit của mạch và bằng 1/2 spps nucleotit của G. Khi gen phiên mã mộ số lần đã lấy từ môi trường nội bào 540 nucleotit loại U. Số lượng nucleotit từng loại trên mARN được tổng hợp từ gen nói trên là:', 'A=180, U=420, X=360, G=240', 'A=840, U=360, X=720, G=480', 'A=180, U=420, X=240, G=360', 'A=420, U=180, X=360, G=240', '4');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Nhận xét nào sau đây không đúng với cơ chế điều hòa hoạt động gen trong operon Lac ở vi khuẩn đường ruột E. coli?', 'Khi môi trường có lactozo và không có lactozo, gen R đều tổng hợp protein ức chế để điều hòa hoạt động của operon Lac.', 'Vùng khởi động là trình tự nucleotit mà enzim ARN polimeraza bám vào để khởi đầu phiên mã.', 'Mõi gen cấu trúc Z, Y, A đều có một vùng điều hòa bao gồm vùng khởi động và vùng vận hành.', 'Vùng vận hành là trình tự nucleotit có thể liên kết với protein ức chế làm ngăn cản sự phiên mã.', '3');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Mỗi gen trong cặp gen dị hợp đều chứa 2998 liên kết photphodieste nối giữa các nucleotit. Gen trội D chứa 17,5% số nucleotit loại T. Gen lặn d có A = G = 25%. Trong trường hợp chỉ xét riêng cặp gen này, tế bào mang kiểu gen Ddd giảm phân bình thường thì loại giao tử nào sau đây không thể được tạo ra?', 'Giao tử có 1275 T', 'Giao tử có 1275 X', 'Giao tử có 525 A', 'Giao tử có 1500 G', '2');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Phân tử mARN sơ khai và mARN trưởng thành được phiên mã từ một gen cấu trúc ở tế bào nhân thực thì loại mARN nào ngắn hơn? Tại sao?', 'Không có loại ARN nào ngắn hơn vì mARN là bản sao của ADN, trên đó làm khuôn mẫu sinh tổng hợp protein', 'mARN trưởng thành ngắn hơn vì sau khi được tổng hợp, mARN sơ khai đã loại bỏ vùng khởi đầu và vùng kết thúc của gen.', 'Không có loại mARN nào ngắn hơn vì sau khi được tổng hợp, mARN sơ khai đã loại bỏ vùng khởi đầu và vùng kết thúc của gen', 'mARN trưởng thành ngắn hơn vì sau khi được tổng hợp, mARN sơ khia đã được loại bỏ các đoạn intron và nối các đoạn exon để tạo thành mARN trưởng thành.', '4');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Trong phòng thí nghiệm, người ta sử dụng 3 loại nucleotit cấu tạo nên ARN để tổng hợp 1 phân tử mARN nhân tạo. Phân tử mARN này chỉ có thể thực hiện được dịch mã khi 3 loại nucleotit được sử dụng là', 'U, G và X', 'G, A và X', 'G, A và U', 'U, X và A', '3');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Nhận định nào sau đây không đúng khi nói về gen cấu trúc?', 'Exon là đoạn ADN mã hóa các axit amin nằm trong vùng điều hòa của gen', 'Vùng kết thúc của gen ở vi khuẩn có những trình tự nucleotit không mã hóa axit amin', 'Mỗi gen cấu trúc có 3 trình tự nucleotit theo thứ tự: vùng điều hòa – mã hóa – kết thúc', 'Vùng điều hào của gen là vùng mang tín hiệu khởi động và kiểm soát phiên mã', '1');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Nhận định nào sau đây là đúng với đột biến cấu trúc đỏa đoạn NST?', 'Đảo đoạn là nguyên nhân gây bệnh ung thư máu ở người', 'Đảo đoạn không làm thay đổi trình tự phân bố gen trên các NST', 'Đảo đoạn làm tăng hoạt tính enzim amilaza ở lúa đại mạch', 'Đảo đoạn góp phần tạo nguồn nguyên liệu cho quá trình tiến hóa.', '4');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Một gen của vi khuẩn E. coli có 120 chu kì xoắn, nhân đôi liên tiếp 3 lần tạo ra các gen con. Mỗi gen con phiên mã 5 lần tạo mARN. Tất cả các phân tử mARN đều tham gia dịch mã và mỗi mARN có 5 riboxom trượt qua một lần. Số chuỗi polipeptit được tồng hợp và số axit amin cần cung cấp cho quá trình dịch mã là', '200 và 80000', '25 và 59850', '200 và 79800', '75 và 29925', '3');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Cơ chế phát sinh đột biến thể lệch bội là do tác nhân gây đột biến', 'làm rối loạn sự phân li của 1 cặp NST ở kì sau của giảm phân', 'làm cho NST bị đứt gãy rồi tái kết hợp bất bình thường', 'làm cho một hoặc một số cặp NST không phân li trong phân bào', 'làm rối loạn quá trình nhân đôi hoặc trao đổi chéo của NST trong phân bào', '3');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Khi nói về đột biến gen, kết luận nào sau đây không đúng?', 'Gen đột biến trội ở dạng dị hợp có thể biểu hiện thành thể đột biến', 'Đột biến gen là nguồn nguyên liệu của tiến hóa và chọn giống', 'Trong quá trình phiên mã nếu lắp ráp sai nguyên tắc bổ sung thì sẽ gây đột biến gen', 'Quá trình nhân đôi ADN không theo nguyên tắc bổ sung thì có thể tạo ra đột biến gen', '3');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Thành phần nào sau đây không thuộc thành phần cấu trúc của operon Lac ở vị trí khuẩn E. coli?', 'Vùng vận hành (O) là nơi protein ức chế có thể liên kết làm ngăn cản sự phiên mã.', 'Gen điều hào R quy định tổng hợp protein ức chế.', 'Vùng khởi động (P) là nơi ARN polimeraza bám vào và khởi đầu phiên mã.', 'Các gen cáu trúc (Z, Y, A) quy định tổng hợp các enzim phân giải đường lactozo.', '2');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Khi nói về quá trình nhân đôi ADN ở tế bào nhân sơ, phát biểu nào sau đây là không đúng?', 'Trong quá trình nhân đôi ADN, enzim nối ligaza tác động lên cả hai mạch đơn mới được tổng hợp từ một phân tử ADN mẹ', 'Sự nhân đôi ADN xảy ra ở nhiều điểm trong mỗi phân tử ADN tạo ra nhiều đơn vị nhân đôi', 'Trong quá trình nhân đôi ADN, enzim ADN polimeraza không tham gia tháo xoắn phân tử ADN', 'Trong quá trình nhân đôi ADN, có sự liên kết bổ sung giữa nucleotit trên mạch gốc với nucleotit của môi trường nội bào theo nguyên tắc A với T, G với X và ngược lại.', '2');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Khi nói về hoạt động của operon Lac, phát biểu nào sau đây không đúng?', 'Đường lactozo làm bất hoạt protein ức chế bằng cách bám vào protein ức chế làm cho cấu trúc không gian của protein ức chế bị thay đổi.', 'Trong một operon Lac, các gen cấu trúc Z, Y, A có số lần nhân đôi và phiên mã khác nhau.', 'Trong một operon Lac, các gen cấu trúc Z, Y, A có số lần nhân đôi và phiên mã bằng nhau.', 'Gen điều hòa và các gen cấu trúc Z, Y, A có số lần nhân đôi bằng nhau.', '2');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Rối loạn phân li cặp NST giới tính trong  giảm phân I ở bố, mẹ giảm phân bình thường, qua thụ tinh tạo ra', 'thể 3X, hội chứng Tơcno', 'thể 3X, hội chứng Claiphento', 'hội chứng Tocno, hội chứng Caliphento', 'hội chứng Đao, hôi chứng Tơcno', '3');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Ở người, sự rối loạn phân li của cặp NST số 21 trong lần phân bào I của giảm phân sẽ tạo ra', 'hai tinh trùng thiếu một NST và hai tinh trùng bình thường', 'hai tinh trùng bình thường, một tinh trùng có hai NST số 21 và một tinh trùng không có NST số 21.', 'hai tình trùng thừ 1 NST và số 21 và hai tinh trùng thừa một NST số 21', 'hai tinh trùng thiếu một NST số 21 và hai tinh trùng thừa 1 NST số 21', '4');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Khi nói về đột biến cấu trúc NST, kết luận nào sau đây là đúng?', 'Mất đoạn NST có độ dài giống nhau ở các NST khác nhau đều biểu hiện kiểu hình giống nhau', 'Mất đoạn NST có độ dài khác nhau ở cùng một vị trí trên một NST biểu hiện kiểu hình giống nhau', 'Các đột biến mất đoạn NST ở các vị trí khác nhau biểu hiện kiểu hình khác nhau', 'Mất đoạn NST ở các vị trí khác nhau trên cùng một NST đều biểu hiện kiểu hình giống nhau', '3');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Một loài thực vật, A – quy định quả đỏ, a – quy định quả vàng. Ở cơ thể lệch bội, hạt phấn (n+1) không cạnh tranh được với hạt phấn (n), còn các loại tế bào noãn đều có khả năng thụ tinh. Phép lai nào dưới đây cho quả vàng chiếm tỉ lệ 1/6?', 'Mẹ Aa x Bố AAa', 'Mẹ Aaa x Bố AA', 'Mẹ AAa x Bố AA', 'Mẹ Aa x Bố Aaa', '4');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Loại đột biến nào sau đây làm thay đổi vị trị của gen trên NST?', 'Đột biến đa bội và đột biến lệch bội', 'Đột biến lệch bội và đột biến chuyển đoạn', 'Đột biến chuyển đoạn và đảo đoạn', 'Đột biến gen và đột biến lệch bội', '3');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Người mắc bệnh hoặc hội chứng nào sau đây là đột biến dạng thể ba?', 'Hội chứng Đao', 'Bệnh ung thư vú', 'Hội chứng Tơcno', 'Bệnh pheninketo niệu', '1');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Khi đề xuất giả thuyết mỗi tính trạng do một một cặp nhân tố di truyền quy định, các nhân tố di truyền trong tế bào không hòa trộn với nhau và phân li đồng đều về các giao tử. Menđen kiểm tra giả thuyết của mình bằng cách nào?', 'Cho F1 lai phân tích', 'Cho F2 tự thụ phấn', 'Cho F1 giao phấn với nhau', 'Cho F1 tự thụ phấn', '1');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Theo Menđen, cơ chế chi phối sự di truyền và biểu hiện của một cặp tính trạng tương phản qua các thế hệ là do', 'sự phân li và tổ hợp của cặp nhân tố di truyền trong giảm phân và thụ tinh', 'sự tổ hợp của cặp NST tương đồng trong thụ tinh', 'sự phân li và tổ hợp của cặp NST tương đồng trong giảm phân và thụ tinh', 'sự phân li và tổ hợp của cặp nhân tố di truyền trong giảm phân', '1');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Điều nào sau đây không đúng với quy luật phân li của Menđen?', 'Mỗi tính trạng của cơ thể do một cặp nhân tố di truyển quy định', 'Mỗi tính trạng của cơ thể do nhiều cặp gen quy định', 'Do sự phân li đồng đều của cặp nhân tố di truyền nên mỗi giao tử chỉ chứa một nhân tố của cặp', 'F1 tuy là cơ thể lai nhưng khi tạo giao tử thì giao tử là thuần khiết', '2');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Cơ sở tế bào học của quy luật phân li là', 'sự phân li và tổ hợp của cặp nhân tố di truyền trong giảm phân và thụ tinh', 'sự phân li của cặp NST tương đồng trong giảm phân', 'sự phân li và tổ hợp của cặp NST tương đồng trong giảm phân và thụ tinh', 'sự tổ hợp của cặp NST trong thụ tinh', '2');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Trong trường hợp gen trội không hoàn toàn, tỉ lệ phân li kiểu hình 1:1 ở F1 sẽ xuất hiện trong kết quả của phép lai nào dưới đây?', 'Aa x Aa', 'aa x aa', 'AA x Aa', 'AA x AA', '3');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Ở người mắt nâu (N) là trội đối với mắt xanh (n). Bố mắt nâu, mẹ mắt nâu, sinh con có đứa mắt nâu có đứa mắt xanh, kiểu gen của bố mẹ sẽ là:', 'Đều có kiểu gen Nn', 'Đều có kiểu gen nn', 'Bố có kiểu gen NN, mẹ có kiểu gen Nn hoặc ngược là', 'Bố có kiểu gen Nn, mẹ có kiểu gen nn hoặc ngược lại', '1');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Khi lai các cây đậu Hà Lan thuần chủng hoa đỏ (AA) với cây hoa trắng (aa) thì kết quả thực nghiệm thu được ở F2 là tỉ lệ 1:2:1 về kiểu gen luôn đi đôi với tỉ lệ 3:1 về kiểu hình. Kết quả trên khẳng định điều nào trong giả thuyết của Menđen là đúng?', 'Thể đồng hợp cho 1 loại giao tửm thể dị hợp cho 2 loại giao tử với tỉ lệ 1:1', 'Mỗi cá thể đời P cho 2 loại giao tử mang gen khác nhau', 'Mõi cá thể đời F1 cho 1 loại giao tử mang gen khác nhau', 'Cơ thể lai F1 cho 2 loại giao tử khác nhau với tỉ lệ 3:1', '1');
insert into QUESTION(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('6', 'Bệnh bạch tạng do một alen lặn nằm trên NST thường quy định, alen trội tương ứng quy định tính trạng bình thường. Trong một gia đình, người bố bị bạch tạng, còn người mẹ bình thường nhưng có bố mắc bệnh bạch tạng. Cặp bố mẹ này sinh con mắc bệnh với xác suất là', '75% con gái', '25% tổng số con', '75% con trai', '50% tổng số con', '4');
