insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một con lắc lò xo dao động điều hòa theo trục x nằm ngang. Lò xo có độ cứng k = 100 N/m. Khi vật có khối lượng m của con lắc đi qua vị trí có li độ x = 4 cm theo chiều âm thì thế năng của con lắc đó là bao nhiêu?', '8 J.', '0,08 J.', '–0,08 J.', '–8 J.', '2');
