insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Con lắc lò xo có chu kì riêng T. Nếu tăng khối lượng của quả cầu lên gấp 4 lần còn lò xo vẫn giữ nguyên như cũ thì chu kì riêng của con lắc sẽ là:', '4T.', '2T.', '0,25T.', '0,5T.', '2');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Lò xo của một con lắc lò xo thẳng đứng bị giãn 4 cm khi vật nặng ở vị trí cân bằng. Lấy g = 10 m/s2, π2 = 10. Chu kì dao động của con lắc là:', '0,4 s.', '4 s.', '10 s.', '100 s.', '1');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một con lắc lò xo dao động điều hòa theo phương thẳng đứng với tần số góc ω = 20 rad/s tại vị trí có gia tốc trọng trường g = 10 m/s2. Khi qua vị trí x = 2 cm, vật có vận tốc v = 40√3 cm/s. Lực đàn hồi cực tiểu của lò xo trong quá trình dao động có độ lớn:', '0,2 N.', '0,1 N.', '0 N.', '0,4 N.', '3');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một con lắc lò xo gồm lò xo có độ cứng k = 10 N/m và vật nặng có khối lượng m = 100 g. Dao động theo phương ngang với biên độ A = 2 cm. Trong mỗi chu kì dao động, khoảng thời gian ngắn nhất mà vật nặng ở những vị trí có khoảng cách với vị trí cấn bằng không nhỏ hơn 1 cm là:', '0,418 s.', '0,209 s.', '0,314 s.', '0,242 s.', '1');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một con lắc lò xo thẳng đứng và một con lắc đơn được tích điện q, cùng khối lượng m. Khi không có điện trường chúng dao động điều hòa với chu kỳ T1 = T2. Khi đặt cả hai con lắc trong cùng một điện trường đều có vectơ cường độ điện trường E nằm ngang thì độ dãn của con lắc lò xo tăng 1,44 lần, con lắc đơn dao động với chu kỳ 5/6s. Chu kỳ dao dộng của con lắc lò xo trong điện trường đều là:', '1,44 s', '1 s', '1,2 s', '5/6 s', '2');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một con lắc lò xo gồm lò xo có chiều dài tự nhiên l0 = 30 cm. Kích thích cho con lắc dao động điều hòa theo phương nằm ngang thì chiều dài cực đại của lò xo là 38 cm. Khoảng cách ngắn nhất giữa hai thời điểm động năng bằng n lần thế năng và thế năng bằng n lần động năng là 4 cm. Giá trị lớn nhất của n gần với giá trị nào nhất sau đây?', '8.', '3.', '5.', '12.', '3');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một con lắc lò xo dao động trên mặt sàn nằm ngang gồm một lò xo nhẹ có độ cứng k = 10 N/m, một đầu gắn cố định, đầu còn lại gắn vào vật khối lượng m = 100g. Hệ số ma sát giữa vật với mặt sàn là µ = 0,1. Ban đầu đưa vật đến vị trí lò xo bị nén một đoạn 7 cm và thả ra. Lấy g = 10 m /s2. Quãng đường vật đi được cho đến khi vật dừng lại là:', '32,5 cm.', '24,5 cm.', '24 cm.', '32 cm.', '2');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Tại một nơi trên mặt đất, một con lắc đơn dao động điều hòa với chu kì 2,2 s. Lấy g = 10 m/s2, π2 = 10. Khi giảm chiều dài dây treo của con lắc 21 cm thì con lắc mới dao động điều hòa với chu kì là', '2,0 s.', '2,5 s.', '1,0 s.', '1,5 s.', '1');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một con lắc đơn có chiều dài 40 cm dao động điều hòa tại nơi có gia tốc trọng trường g = 10 m/s2 với biên độ góc 0,02 rad. Tốc độ của con lắc khi dây treo thẳng đứng là:', '4 cm/s.', '4 m/s.', '10 cm/s.', '10 m/s.', '1');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một con lắc đơn có chiều dài 121 cm, dao động điều hòa tại nơi có gia tốc trọng trường g. Lấy π2 = 10. Chu kì dao động của con lắc là:', '0,5 s.', '2 s.', '1 s.', '2,2 s.', '4');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một con lắc đơn gồm một dây kim loại nhẹ dài 1 m, dao động điều hoà với biên độ góc 0,2 rad trong một từ trường đều mà cảm ứng từ có hướng vuông góc với mặt phẳng dao động của con lắc và có độ lớn 1T. lấy gia tốc trọng trường 10 m/s2.  Tính suất điện động cực đại xuất hiện trên thanh treo con lắc:', '0,45 V.', '0,63 V.', '0,32 V.', '0,22 V.', '3');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Hai con lắc đơn (với tần số góc dao động điều hòa lần lượt là 10π/9 rad/s và 10π/8 rad/s) được treo ở trần một căn phòng. Khi các vật nhỏ của hai con lắc đang ở vị trí cân bằng, đồng thời truyền cho chúng các vận tốc cùng hướng sao cho hai con lắc dao động điều hòa với cùng biên độ góc, trong hai mặt phẳng song song với nhau. Tìm khoảng thời gian kể từ lúc truyền vận tốc đến lúc hai dây treo song song nhau lần thứ 2014.', '1611,5 s.', '14486,4 s.', '14486,8 s.', '14501,2 s.', '1');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một con lắc đơn dao động điều hòa với chu kì T = 4 s, thời gian để con lắc đi từ vị trí cân bằng đến vị trí có li độ cực đại là:', '2 s.', '1,5 s.', '1 s.', '0,5 s.', '3');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Hai con lắc đơn dao động điều hòa tại cùng một vị trí trên Trái Đất. Chiều dài và chu kì dao động của con lắc đơn lần lượt là l1, l2 và T1, T2. Biết T2 = 2T1. Hệ thức đúng là:', 'l1= 2l2.', 'l1= 4l2.', 'l2 = 4l1.', 'l2 = 2l1.', '2');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một con lắc đơn gồm dây treo có chiều dài 1 m và vật nhỏ có khối lượng 100 g mang điện tích 2.10-5 C. Treo con lắc đơn này trong điện trường đều với vectơ cường độ điện trường hướng theo phương ngang và có độ lớn 5.104 V/m. Trong mặt phẳng thẳng đứng đi qua điểm treo và song song với vectơ cường độ điện trường, kéo vật nhỏ theo chiều của vectơ cường độ điện trường sao cho dây treo hợp với vectơ gia tốc trọng trường  một góc 54° rồi buông nhẹ cho con lắc dao động điều hòa. Lấy g = 10 m/s2. Trong quá trình dao động, tốc độ cực đại của vật nhỏ là :', '0,59 m/s.', '3,41 m/s.', '2,87 m/s.', '0,50 m/s.', '1');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Có hai con lắc đơn giống nhau. Vật nhỏ của con lắc thứ nhất mang điện tích 2,45.10-6 C, vật nhỏ con lắc thứ hai không mang điện. Treo cả hai con lắc vào vùng điện trường đều có đường sức điện thẳng đứng, và cường độ điện trường có độ lớn E = 4,8.104 V/m. Xét hai dao động điều hòa của con lắc, người ta thấy trong cùng một khoảng thời gian, con lắc thứ nhất thực hiện được 7 dao động thì con lắc thứ hai thực hiện được 5 dao động. Lấy g = 9,8 m/s2. Khối lượng vật nhỏ của mỗi con lắc là:', '12,5 g', '4,054 g', '42 g', '24,5 g', '1');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một con lắc lò xo thẳng đứng và một con lắc đơn được tích điện q, cùng khối lượng m. Khi không có điện trường chúng dao động điều hòa với chu kỳ T1 = T2. Khi đặt cả hai con lắc trong cùng một điện trường đều có vectơ cường độ điện trường E nằm ngang thì độ dãn của con lắc lò xo tăng 1,44 lần, con lắc đơn dao động với chu kỳ 5/6 s. Chu kỳ dao dộng của con lắc lò xo trong điện trường đều là:', '1,44 s.', '1 s.', '1,2 s.', '5/6 s.', '2');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một con lắc đơn mà quả cầu có khối lượng 0,5kg dao động nhỏ với chu kỳ 0,4π (s) tại nơi có gia tốc rơi tự do g = 10 m/s2. Biết li độ góc cực đại là 0,15 rad. Tính cơ năng dao động.', '30 mJ', '4 mJ', '22,5 mJ', '25 mJ', '3');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Truyền cho quả nặng của con lắc đơn đang đứng yên ở vị trí cân bằng một vận tốc v0 = 1/3 m/s  theo phương ngang thì nó dao động điều hòa với biên độ góc α0 = 6°. Lấy. Chu kỳ dao động của con lắc bằng:', '2,00s.', '2,60s.', '30,0ms.', '2,86s.', '1');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một con lắc đơn được treo vào một điện trường đều có đường sức thẳng đứng. Khi quả nặng của con lắc được tích điện q1 thì chu kì dao động điều hòa của con lắc là 1,6s. Khi quả nặng của con lắc được tích điện q2 = - q1 thì chu kì dao động điều hòa của con lắc là 2,5s. Khi quả nặng của con lắc không mang điện thì chu kì dao động điều hòa của con con lắc là:', '2,84s', '2,78s', '2,61s', '1,91s', '4');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một con lắc đơn có khối lượng vật nặng m = 200g, chiều dài l = 50 cm. Từ vị trí cân bằng ta truyền cho vật nặng vận tốc v = 1 m/s theo phương nằm ngang. Lấy g = π2 = 10 m/s2. Lực căng dây khi vật đi qua vị trí cân bằng là:', '6N', '4N', '3N', '2,4N', '4');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một con lắc đơn có chiều dài 80 cm dao động tại nơi có g = 10 m/s2. Biết rằng lực căng dây của dây treo có giá trị cực đại gấp 4 lần giá trị cực tiểu. Chọn mốc thế năng tại vị trí cân bằng, bỏ qua lực cản. Tốc độ của vật nặng tại thời điểm động năng bằng thế năng là:', '2π/3 m/s', '2 m/s', 'π m/s', '1 m/s', '2');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một con lắc đơn được treo vào trần một thang máy. Khi thang máy chuyển động thẳng đứng đi lên trên nhanh dần đều với gia tốc có độ lớn a thì con lắc dao động điều hòa với chu kỳ là 3s. Khi thang máy chuyển động thẳng đứng đi lên chậm dần đều cũng với gia tốc có độ lớn là a thì chu kỳ dao động điều hòa của con lắc là 4s. Khi thang máy đứng yên thì chu kỳ dao động điều hòa của con lắc là:', '5,0 s', '2,4 s', '3,5 s', '3,4 s', '4');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một con lắc đơn có chu kỳ T = 1 s khi đặt trong chân không. Quả lắc làm bằng một hợp kim khối lượng riêng D = 8,67 g/cm3. Coi sức cản của không khí không đáng kể (con lắc vẫn dao động điều hòa). Hỏi chu kỳ của con lắc thay đổi thế nào, biết khối lượng riêng của không khí là d = 1,31 g/l.', 'Tăng 7,5.10-5 s', 'Giảm 7,5.10-5 s', 'Tăng 1,5.10-4 s', 'Giảm 1,5.10-4 s', '1');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Con lắc lò xo gồm vật nặng 100 gam và lò xo nhẹ độ cứng 40 N/m. Tác dụng một ngoại lực điều hòa cưỡng bức biên độ F và tần số f1 = 4 Hz theo phương trùng với trục của lò xo thì biên độ dao động ổn định A1. Nếu giữ nguyên biên độ F và tăng tần số ngoại lực đến giá trị f2 = 5 Hz thì biên độ dao động ổn định A2. So sánh A1 và A2.', 'A1 = 2A2.', 'A1 = A2.', 'A1 < A2.', 'A1 > A2.', '4');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một chất điểm đang dao động điều hòa. Khi vừa qua khỏi vị trí cân bằng một đoạn S động năng của chất điểm là 0,091 J. Đi tiếp một đoạn 2S thì động năng chì còn 0,019 J và nếu đi thêm một đoạn S (biết A > 3S) nữa thì động năng bây giờ là:', '0,042 J.', '0,096 J.', '0,036 J.', '0,032 J.', '3');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một con lắc lò xo dao động tắt dần. Cứ sau mỗi chu kì, biên độ dao động của nó giảm 2,5%. Phần năng lượng của con lắc bị mất đi sau mỗi chu kì là:', '5%', '7,5%', '6%', '9,5%', '1');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Vỏ máy của một động cơ nổ rung mạnh dần lên khi trục quay động cơ tăng dần tốc độ quay đến tốc độ 1440 vòng/phút và giảm rung động đi khi tăng tiếp tốc độ quay động cơ. Tần số riêng của dao động vỏ máy là:', '1400 vòng/phút', '1440 vòng/phút', '1380 vòng/phút', '1420 vòng/phút.', '2');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Cơ năng của một dao động tắt dần giảm 5% sau mỗi chu kì. Biên độ dao động tắt dần sau mỗi chu kì giảm đi:', '5%', '2,5%', '10%', '√5% ≈ 2,24%', '2');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Biên độ của một dao động tắt dần giảm 1,5% sau mỗi chu kì. Trong một dao động toàn phần, cơ năng của dao động bị mất đi:', '3%', '4,5%', '0,75%', '2,25%.', '1');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một người xách một xô nước đi trên đường, mỗi bước đi dài 45 cm thì nước trong xô bị sóng sánh mạnh nhất. Chu kì dao động riêng của nước trong xô là 0,3 s. Vận tốc bước đi của người đó là:', '5,4 km/h', '3,6 km/h', '4,8 km/h', '4,2 km/h', '1');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một vật tham gia đồng thời 2 dao động điều hoà cùng phương cùng tần số x1 = 5cos(4t + φ1) cm, x2 = 3cos(4t + φ2) cm. Biên độ dao động tổng hợp thoả mãn:', '2 cm ≤ A≤ 4 cm.', '5 cm ≤ A≤ 8 cm.', '3 cm ≤ A≤ 5 cm.', '2 cm ≤ A≤ 8 cm.', '4');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Hai dao động điều hòa cùng phương, cùng tần số có biên độ lần lượt là A1 = 8 cm; A2 = 15 cm và lệch pha nhau π/2. Dao động tổng hợp của hai dao động này có biên độ bằng:', '23 cm.', '7 cm.', '11 cm.', '17 cm.', '4');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một vật thực hiện đồng thời hai dao động điều hòa cùng phương x1 = 4√3cos10πt (cm) và i, t đo bằng giây. Vận tốc của vật tại thời điểm t = 2s là:', '20π (cm/s)', '40π (cm/s)', '10π (cm/s)', '40π (cm/s)', '2');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Chuyển động của một vật là hai dao động điều hòa cùng phương. Hai dao động này có phương trình lần lượt là x1 = 4cos(10t - π/6) (cm) và x2 = A2cos(10t - π/6) (cm). Độ lớn của vận tốc ở vị trí cân bằng là 60 cm/s. Giá trị của A2 bằng:', '4 cm.', '6 cm.', '2 cm.', '8 cm.', '3');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Dao động của một chất điểm là tổng hợp của hai dao động điều hòa cùng phương, có phương trình li độ lần lượt là x1 = 3cos(2πt/3 - π/2) (cm) và x2 = 3√3cos(2πt/3) (cm) (x1 và x2 tính bằng cm, t tính bằng s). Tại các thời điểm x1 = x2 li độ dao động tổng hợp là:', '± 7,59 cm.', '± 5,19 cm.', '± 6 cm.', '± 3 cm.', '2');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Tổng hợp hai dao động điều hoà cùng phương, cùng tần số, cùng biên độ a là một dao động có biên độ cũng bằng a thì 2 dao động thành phần có độ lệch pha là:', 'π/2', 'π/4', 'π/3', '2π/3', '4');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Tổng hợp hai dao động điều hoà cùng phương, cùng tần số, cùng biên độ a là một dao động có biên độ a√2 thì 2 dao động thành phần có độ lệch pha là:', 'π/2', 'π/4', '0.', 'π', '1');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một vật tham gia đồng thời hai dao động điều hoà cùng phương có phương trình lần lượt là x1 = 5cos(5πt + φ1) (cm); x2 = 5cos(5πt + φ2) (cm) với 0 ≤ φ1 – φ2 ≤ π. Biết phương trình dao động tổng hợp x = 5cos(5πt + π/6) (cm). Hãy xác định φ1.', 'π/6.', '–π/6.', 'π/2.', '0.', '3');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một chất điểm tham gia đồng thời hai dao động điều hòa trên cùng một trục Ox có phương trình: x1 = 4cos(ωt + π/3) cm, x2 = A2cos(ωt + φ2) cm. Phương trình dao động tổng hợp x = 2cos(ωt + φ) cm. Biết φ – φ2 = π/2. Cặp giá trị nào của A2 và φ sau đây là đúng?', '3√3 cm và 0.', '2√3 cm và π/4.', '3√3  cm và π/2.', '2√3 cm và 0.', '4');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Cho hai dao động điều hòa cùng phương có phương trình x1 = 3cos10πt (cm) và x2 = 4cos(10πt + 0,5π) (cm). Dao động tổng hợp của hai dao động này có biên độ là:', '1 cm.', '3 cm.', '5 cm.', '7 cm.', '3');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một tham gia đồng thời vào hai dao động điều hòa có phương trình x1 = 4√3cos(10πt) cm và x2 = 4sin(10πt) cm. Vận tốc của vật khi t = 2 s là:', '123 cm/s.', '120,5 cm/s.', '– 123 cm/s.', '125,7 cm/s.', '4');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một con lắc lò xo gồm vật nhỏ có khối lượng m và lò xo có độ cứng k, dao động điều hòa. Nếu tăng độ cứng k lên 2 lần và giảm khối lượng m đi 8 lần thì tần số dao động của con lắc sẽ:', 'tăng 4 lần.', 'giảm 2 lần.', 'tăng 2 lần.', 'giảm 4 lần.', '1');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một vật dao động theo phương trình x = 5cos(5πt + 0,5π) cm. Biên độ dao động của vật là:', '2,5 cm.', '0,5 cm.', '10 cm.', '5 cm.', '4');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một bạn học sinh dùng đồng hồ bấm giây để đo chu kỳ dao động của con lắc đơn bằng cách xác định khoảng thời gian để con lắc thực hiện được 10 dao động toàn phần. Kết quả 4 lần đo liên tiếp của bạn học sinh này là: 21,2 s; 20,2 s; 20,9 s; 20,0 s . Biết sai số tuyệt đối khi dùng đồng hồ này là 0,2 s (bao gồm sai số ngẫu nhiên khi bấm và sai số dụng cụ). Theo kết quả trên thì cách viết giá trị của chu kỳ T nào sau đây là đúng nhất?', 'T = 2,06 ± 0,2 s.', 'T = 2,13 ± 0,02 s.', 'T = 2,00 ± 0,02 s.', 'T = 2,06 ± 0,02 s.', '4');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Hai dao động điều hòa cùng phương, cùng tần số có biên độ lần lượt là A1 = 8 cm; A2 = 15 cm và lệch pha nhau 0,5π. Dao động tổng hợp của hai dao động này có biên độ bằng:', '7 cm.', '23 cm.', '11 cm.', '17 cm.', '4');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một con lắc lò xo treo vào một điểm cố định, dao động điều hòa theo phương thẳng đứng với chu kỳ 2,4 s. Trong một chu kỳ, nếu tỉ số của thời gian lò xo giãn với thời gian lò xo nén bằng 2 thì thời gian mà lực đàn hồi tác dụng lên vật ngược chiều lực kéo về là:', '0,4 s.', '0,2 s.', '0,3 s.', '0,1 s.', '1');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một vật dao động điều hòa theo phương trình x = 6cos2πt cm, biên độ dao động của vật là:', 'A = 6 mm.', 'A = 6 cm.', 'A = 12 cm.', 'A = 12π cm.', '2');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Gắn một vật m1 = 4 kg vào một lò xo thì nó dao động với chu kì T1 = 1 s. Khi gắn một vật khác có khối lượng m2 vào lò xo trên thì nó dao động với chu kì T2 = 0,5s. Khối lượng vật m2 bằng:', '2 kg.', '1 kg.', '8 kg.', '16 kg.', '2');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một con lắc đơn có chiều dài dây treo là 64 cm. Lấy g = π2 m/s2. Số dao động toàn phần vật thực hiện được trong 24 giây là:', '15.', '10.', '1,5.', '25.', '3');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Vận tốc của một vật dao động điều hòa có phương trình v=20cos10t cm/s. Khối lượng của vật là m = 500 g. Hợp lực tác dụng lên vật có giá trị cực đại là:', '105 N.', '100 N.', '10 N.', '1 N.', '4');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một người xách một xô nước đi trên đường, mỗi bước đi dài 40 cm. Chu kỳ dao động riêng của nước trong xô là 0,2 s. Để nước trong xô sóng sánh mạnh nhất thì người đó phải đi với tốc độ:', '4 m/s.', '2 m/s.', '80 m/s.', '40 m/s.', '1');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một vật nặng gắn vào lò xo có độ cứng k = 200 N/m, dao động điều hòa với biên độ A = 10 cm. Khi vật nặng cách vị trí biên 6 cm nó sẽ có động năng:', '8400 J.', '0,84 J.', '0,16 J.', '0,64 J.', '2');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một con lắc lò xo có khối lượng vật nhỏ là m1 = 300 g dao động điều hòa với chu kì 1 s. Nếu thay vật nhỏ có khối lượng m1 bằng vật nhỏ có khối lượng m2 thì con lắc dao động với chu kì 0,5 s. Giá trị m2 bằng:', '150 g.', '75 g.', '25 g.', '100 g.', '2');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Một sóng cơ tần số 25 Hz truyền dọc theo trục Ox với tốc độ 100 cm/s. Hai điểm gần nhau nhất trên trục Ox mà các phần tử sóng tại đó dao động ngược pha nhau, cách nhau:', '2 cm.', '3 cm.', '4 cm.', '1 cm.', '1');
insert into question(subjectId, questionName, answer1, answer2, answer3, answer4, answer) values ('5', 'Trên mặt nước ba nguồn sóng u1 = 2acosωt, u2 = 3acosωt, u3 = 4acosωt đặt tại A, B và C sao cho tam giác ABC vuông cân tại C và AB = 12 cm. Biết biên độ sóng không đổi và bước sóng lan truyền 2 cm. Điểm M trên đoạn CO (O là trung điểm AB) cách O một đoạn ngắn nhất bằng bao nhiêu thì nó dao động với biên độ 9a.', '1,1 cm.', '0,93 cm', '1,75 cm.', '0,57 cm.', '2');